// flutter pub add get_itimport 'package:get_it/get_it.dart';import 'package:tasky/Features/AddTask/domain/use_cases/new_task_use_case.dart';import 'package:tasky/Features/Authentication/data/repositories/authentication_repo.dart';import 'package:tasky/Features/Authentication/domain/repositories/authentication_repo_impl.dart';import 'package:tasky/Features/Authentication/domain/use_cases/authentication_use_cases.dart';import 'package:tasky/Features/Home/domain/use_cases/home_use_case.dart';import 'package:tasky/Features/QR/domain/use_cases/qr_use_case.dart';import 'package:tasky/Features/User/data/repositories/user_reo.dart';import 'package:tasky/Features/User/domain/use_cases/user_use_case.dart';import '../../Features/AddTask/data/repositories/new_task_repo.dart';import '../../Features/AddTask/domain/repositories/new_task_repo_impl.dart';import '../../Features/Home/data/repositories/home_repo.dart';import '../../Features/Home/domain/repositories/home_repo_impl.dart';import '../../Features/User/domain/repositories/user_repo_impl.dart';import '../../Features/QR/data/repositories/qr_repo.dart';import '../../Features/QR/domain/repositories/qr_repo_impl.dart';final GetIt getIt = GetIt.instance;void setupService() {  getIt.registerLazySingleton<AuthenticationRepo>(      () => AuthenticationRepoImpl());  getIt.registerLazySingleton<UserRepo>(() => UserRepoImpl());  getIt.registerLazySingleton<NewTaskRepo>(() => NewTaskRepoImpl());  getIt.registerLazySingleton<QrRepo>(() => QrRepoImpl());  getIt.registerLazySingleton<HomeRepo>(() => HomeRepoImpl());  //------------------------------------------------------------------  getIt.registerLazySingleton<LoginUseCase>(      () => LoginUseCase(getIt<AuthenticationRepo>()));  getIt.registerLazySingleton<SignUpUseCase>(      () => SignUpUseCase(getIt<AuthenticationRepo>()));  /////////////////////////////////////////////////  getIt      .registerLazySingleton<UserUseCase>(() => UserUseCase(getIt<UserRepo>()));  ////////////////////////////////////////////////  getIt.registerLazySingleton<UploadImageUseCase>(      () => UploadImageUseCase(getIt<NewTaskRepo>()));  getIt.registerLazySingleton<CreateTaskUseCase>(      () => CreateTaskUseCase(getIt<NewTaskRepo>()));  ////////////////////////////////////////////////  getIt.registerLazySingleton<QrUseCase>(() => QrUseCase(getIt<QrRepo>()));  ////////////////////////////////////////////////  getIt.registerLazySingleton<GetAllTasksUseCase>(      () => GetAllTasksUseCase(getIt<HomeRepo>()));  getIt.registerLazySingleton<RefreshTokenUseCase>(      () => RefreshTokenUseCase(getIt<HomeRepo>()));  getIt.registerLazySingleton<LogoutUseCase>(      () => LogoutUseCase(getIt<HomeRepo>()));  getIt.registerLazySingleton<RemoveTaskUseCase>(      () => RemoveTaskUseCase(getIt<HomeRepo>()));  getIt.registerLazySingleton<EditTaskUseCase>(      () => EditTaskUseCase(getIt<HomeRepo>()));}