import 'package:flutter/material.dart';import 'string.dart';class AppList{  static List<DropdownMenuItem<String>> itemsStatus() {    return const [      DropdownMenuItem(        value: AppStrings.statusP,        child: Text(AppStrings.statusP),      ),      DropdownMenuItem(        value: AppStrings.statusW,        child: Text(AppStrings.statusW),      ),      DropdownMenuItem(        value: AppStrings.statusF,        child: Text(AppStrings.statusF),      ),    ];  }  static List<DropdownMenuItem<String>> itemsPriority() {    return const [      DropdownMenuItem(        value: 'low',        child: Text('Low Priority'),      ),      DropdownMenuItem(        value: 'medium',        child: Text('Medium Priority'),      ),      DropdownMenuItem(        value: 'high',        child: Text('High Priority'),      ),    ];  }}