import 'package:flutter/material.dart';import 'string.dart';class AppList {  static List<DropdownMenuItem<String>> itemsStatus() {    return const [      DropdownMenuItem<String>(        value: AppStrings.statusPv,        child: Text(AppStrings.statusP),      ),      DropdownMenuItem<String>(        value: AppStrings.statusWv,        child: Text(AppStrings.statusW),      ),      DropdownMenuItem<String>(        value: AppStrings.statusFv,        child: Text(AppStrings.statusF),      ),    ];  }  static List<DropdownMenuItem<String>> itemsPriority() {    return const [      DropdownMenuItem(        value: AppStrings.lowV,        child: Text(AppStrings.low),      ),      DropdownMenuItem(        value: AppStrings.mediumV,        child: Text(AppStrings.medium),      ),      DropdownMenuItem(        value: AppStrings.highV,        child: Text(AppStrings.high),      ),    ];  }}