import 'package:intl/intl.dart';extension StringExtension on String {  String get formatDate {    DateTime dateTime = DateTime.parse(this);    final day = dateTime.day;    final month = DateFormat.MMMM().format(dateTime);    final year = dateTime.year;    return "$day $month,$year";  }  String get formatDate2 {    DateTime dateTime = DateTime.parse(this);    final day = dateTime.day;    final month = dateTime.month;    final year = dateTime.year;    return "$day/$month/$year";  }}