import 'package:flutter/material.dart';extension PaddingExtension on Widget {  Widget  paddingAll(double value) =>      Padding(padding: EdgeInsetsDirectional.all(value), child: this,);  // Widget  paddingS(double value) =>  //     Padding(padding: EdgeInsetsDirectional.symmetric(horizontal: value), child: this,);  Widget  paddingSH(double value) =>      Padding(padding: EdgeInsetsDirectional.symmetric(horizontal: value), child: this,);  Widget  paddingSV(double value) =>      Padding(padding: EdgeInsetsDirectional.symmetric(vertical: value), child: this,);}