import 'package:flutter/material.dart';import 'package:cached_network_image/cached_network_image.dart';const Icon buildIcon = Icon(Icons.error);class BuildImageCash extends StatelessWidget {  final String urlImage;  const BuildImageCash({super.key, required this.urlImage});  @override  Widget build(BuildContext context) {    return CachedNetworkImage(      imageUrl: urlImage,      placeholder: (context, url) => const CircularProgressIndicator(),      errorWidget: (context, url, error) => buildIcon,    );  }}