import 'package:flutter/material.dart';import 'package:intl_phone_number_input/intl_phone_number_input.dart';import 'package:tasky/Core/Resources/styles.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import '../../../../Core/Resources/colors.dart';class CustomInternationalPhoneNumberInput extends StatelessWidget {  final TextEditingController controller;  @override  Widget build(BuildContext context) {    PhoneNumber deNumber = PhoneNumber(isoCode: 'EG');    return InternationalPhoneNumberInput(      onInputChanged: (PhoneNumber number) {        controller.text = number.phoneNumber.toString();      },      selectorConfig: const SelectorConfig(        selectorType: PhoneInputSelectorType.BOTTOM_SHEET,        useBottomSheetSafeArea: true,      ),      textStyle: getTextField(),      ignoreBlank: false,      autoValidateMode: AutovalidateMode.disabled,      selectorTextStyle: getTextField().copyWith(fontWeight: FontWeight.w700),      initialValue: deNumber,      formatInput: true,      keyboardType:          const TextInputType.numberWithOptions(signed: true, decimal: true),      inputBorder: OutlineInputBorder(          borderRadius: BorderRadius.circular(20),          borderSide: const BorderSide(color: AppColors.colorTextFieldBorder)),    ).paddingSH(20);  }  const CustomInternationalPhoneNumberInput(      {super.key, required this.controller});}