import 'package:flutter/material.dart';import 'package:tasky/Core/Utils/Extensions/size_box.dart';import '../../../Config/Route/route_app.dart';import '../../Resources/string.dart';import '../../Resources/styles.dart';//Parameterclass BuildBottomAuthentication extends StatelessWidget {  final bool isLogIn;  const BuildBottomAuthentication({    super.key,    required this.isLogIn,  });  @override  Widget build(BuildContext context) {    return Column(      children: [        Row(          mainAxisAlignment: MainAxisAlignment.center,          children: [            Text(isLogIn ? AppStrings.noAccountText : AppStrings.hasAccountText,                style: getMediumText()),            5.sW,            InkWell(                onTap: () {                  isLogIn                      ? Navigator.pushNamed(context, Routes.routeCreateAccount)                      : Navigator.pushNamed(context, Routes.routeLogin);                },                child: Text(                  isLogIn ? AppStrings.createAccount : AppStrings.login,                  style: getMediumText().copyWith(fontWeight: FontWeight.w600),                )),          ],        ),      ],    );  }}