import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';class BuildImageAssets extends StatelessWidget {  final String url;  final bool typeSVg;  final double? height;  final double? width;  const BuildImageAssets(      {super.key,      required this.url,      required this.typeSVg,      this.height,      this.width});  @override  Widget build(BuildContext context) {    if (typeSVg) {      return SvgPicture.asset(        height: height,        width: width,        url,      );    } else {      return Image.asset(        height: height,        width: width,        url,      );    }  }}