import 'dart:ui';import 'package:flutter/material.dart';void showSnackBar(BuildContext context, String message) {  ScaffoldMessenger.of(context).showSnackBar(    SnackBar(      padding: const EdgeInsets.all(20),      shape: const ContinuousRectangleBorder(        borderRadius: BorderRadius.all(Radius.circular(10)),      ),      content: Stack(        children: [          // BackdropFilter(          //   filter: ImageFilter.blur(sigmaX: 10.0, sigmaY: 10.0),          //   child: Container(          //     color: Colors.black,          //   ),          // ),          Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text(                message, // style: Styles.textStyle20.copyWith(color: Colors.white),              ),            ],          ),        ],      ),      // backgroundColor: Colors.transparent,      elevation: 8.0,      behavior: SnackBarBehavior.fixed,    ),  );}