import 'package:flutter/material.dart';import 'package:qr_flutter/qr_flutter.dart';class QrBuilding extends StatelessWidget {  const QrBuilding({    super.key,    required this.item,  });  final String item;  @override  Widget build(BuildContext context) {    return Align(      child: QrImageView(        data: item,        version: QrVersions.auto,        size: MediaQuery.of(context).size.height * .4,      ),    );  }}