class AddTaskModel {  AddTaskModel({    this.image,    this.title,    this.desc,    this.priority,  });  AddTaskModel.fromJson(dynamic json) {    image = json['image'];    title = json['title'];    desc = json['desc'];    priority = json['priority'];  }  String? image;  String? title;  String? desc;  String? priority;  Map<String, dynamic> toJson() {    final map = <String, dynamic>{};    map['image'] = image;    map['title'] = title;    map['desc'] = desc;    map['priority'] = priority;    return map;  }}