import 'dart:io';import 'package:dartz/dartz.dart';import 'package:dio/dio.dart';import 'package:http_parser/http_parser.dart';import 'package:tasky/Core/Network/Local/local_string.dart';import 'package:tasky/Core/Utils/Errors/failure.dart';import '../../../../Core/Network/Remote/dio.dart';import '../../../../Core/Network/Remote/endpoints.dart';import '../../data/models/add_task_model.dart';import '../../data/repositories/task_repo.dart';class AddTaskRepoImpl implements AddTaskRepo {  @override  Future<Either<Failure, bool>> createNewTask(      {required AddTaskModel model}) async {    try {      await DioHelper.postData(        path: AppEndPoint.createTask,        token: GetLocalValue.token,        data: model,      );      return right(true);    } catch (e) {      if (e is DioException) {        return left(ServerFailure.fromDioError(e));      } else {        return left(ServerFailure.fromResponse(e.hashCode, e));      }    }  }  @override  Future<Either<Failure, String>> uploadImage({required File image}) async {    try {      String fileName = image.path.split('/').last;      String fileExtension = fileName.split('.').last.toLowerCase();      if (!['jpg', 'jpeg', 'png', 'gif'].contains(fileExtension)) {        return left(            ServerFailure.fromResponse(422, 'File is not a valid image.'));      }      FormData formData = FormData.fromMap({        'image': await MultipartFile.fromFile(          image.path,          filename: fileName,          contentType: MediaType('image', fileExtension),        ),      });      var response = await DioHelper.postData(          path: AppEndPoint.uploadImage, data: formData, token: GetLocalValue.token);      if (response.statusCode == 201) {        print('51: ${response.data['image'].toString()}');      }      return right(response.data['image'].toString());    } catch (e) {      if (e is DioException) {        return left(ServerFailure.fromDioError(e));      } else {        return left(ServerFailure.fromResponse(e.hashCode, e.toString()));      }    }  }}