import 'package:flutter/material.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import 'package:tasky/Core/Utils/Extensions/size_box.dart';import 'package:tasky/Features/AddNewTask/presentation/manager/new_task_cubit.dart';import '../../../../Core/Resources/list.dart';import '../../../../Core/Resources/string.dart';import '../../../../Core/Resources/styles.dart';import '../../../../Core/Resources/validate.dart';import '../../../../Core/Utils/Widget/TextField/build_text_field.dart';import '../../../../Core/Utils/Widget/build_button.dart';import '../../../TaskDetails/presentation/widgets/drop_down_button_form_field.dart';import 'data_picker.dart';import 'image_picker.dart';class FormAddTask extends StatefulWidget {  const FormAddTask({super.key});  @override  State<FormAddTask> createState() => _FormAddTaskState();}class _FormAddTaskState extends State<FormAddTask> {  final key = GlobalKey<FormState>();  TextEditingController? controllerTitle = TextEditingController();  TextEditingController? controllerDescription = TextEditingController();  @override  Widget build(BuildContext context) {    return Form(        key: key,        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            20.sH,            const ImagePickerWidget(),            Text(              'Task title',              style: getSmallText2(),            ).paddingSH(15),            DefaultTextFormField(              inputValidator: (p0) => defaultValidate(                  text: p0.toString(), validate: AppStrings.otherValidate),              controller: controllerTitle,              text: 'Enter title here...',              type: TextInputType.text,            ),            Text(              'Task title',              style: getSmallText2(),            ).paddingSH(15),            DefaultTextFormField(              inputValidator: (p0) => defaultValidate(                  text: p0.toString(), validate: AppStrings.otherValidate),              controller: controllerDescription,              text: 'Enter description here...',              type: TextInputType.text,              maxLine: 8,            ),            Text(              'Priority',              style: getSmallText2(),            ).paddingSH(15),            DropdownButtonFormFieldTasks(              status: 'low',              items: AppList.itemsPriority(),            ).paddingSH(15),            Text(              'Due date',              style: getSmallText2(),            ).paddingSH(15).paddingSV(15),            const DatePickerTextField(),            Center(              child: DefaultButton(                press: () {                  if (key.currentState!.validate()) {                    NewTaskCubit.get(context).createTask(context,                        title: controllerTitle!.text,                        desc: controllerDescription!.text);                  }                },                text: 'Add Task',                condition: true,                width: MediaQuery.of(context).size.width * .8,              ),            )          ],        ));  }}