import 'dart:io';import 'package:dartz/dartz.dart';import 'package:tasky/Core/Utils/Errors/failure.dart';import '../../data/models/new_task_model.dart';import '../../data/repositories/new_task_repo.dart';class UploadImageUseCase {  final AddTaskRepo addTaskRepo;  UploadImageUseCase(this.addTaskRepo);  Future<Either<Failure, String>> execute({required File image}) {    return addTaskRepo.uploadImage(image: image);  }}class CreateTaskUseCase {  final AddTaskRepo addTaskRepo;  CreateTaskUseCase(this.addTaskRepo);  Future<Either<Failure, bool>> execute({required NewTaskModel model}) {    return addTaskRepo.createNewTask(model: model);  }}