import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Config/Route/next_screen.dart';import 'package:tasky/Core/Resources/icons.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Features/Home/data/models/task_model.dart';import 'package:tasky/Features/Home/presentation/manager/home_cubit.dart';import '../widgets/form_edit.dart';class EditScreen extends StatefulWidget {  final TaskModel item;  const EditScreen({super.key, required this.item});  @override  State<EditScreen> createState() => _EditScreenState();}class _EditScreenState extends State<EditScreen> with SingleTickerProviderStateMixin {  late AnimationController _controller;  late Animation<Offset> _offsetAnimation;  @override  void initState() {    super.initState();    _controller = AnimationController(      duration: const Duration(milliseconds: 500),      vsync: this,    )..forward();    _offsetAnimation = Tween<Offset>(      begin: const Offset(0.0, 1.0),      end: Offset.zero,    ).animate(CurvedAnimation(      parent: _controller,      curve: Curves.easeOut,    ));  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return BlocConsumer<HomeCubit, HomeState>(      listener: (context, state) {        if (state is UpdateTaskSuccess) {          HomeCubit.get(context).getListTasks();        }        if (state is HomeSuccess) {          popScreen(context);        }      },      builder: (context, state) {        return Scaffold(          appBar: AppBar(            title: const Text(AppStrings.editeINAppBar),            actions: [              IconButton(                  onPressed: () => HomeCubit.get(context).editTask(                    context,                    widget.item,                  ),                  icon: AppIcons.save)            ],          ),          body: SafeArea(            child: SlideTransition(              position: _offsetAnimation,              child: SingleChildScrollView(                child: FormEdit(item: widget.item),              ),            ),          ),        );      },    );  }}