import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Config/Route/next_screen.dart';import 'package:tasky/Core/Resources/icons.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Features/Home/data/models/task_model.dart';import 'package:tasky/Features/Home/presentation/manager/home_cubit.dart';import '../widgets/form_edit.dart';class EditScreen extends StatelessWidget {  final TaskModel item;  const EditScreen({super.key, required this.item});  @override  Widget build(BuildContext context) {    return BlocConsumer<HomeCubit, HomeState>(      listener: (context, state) {        if (state is UpdateTaskSuccess) {          HomeCubit.get(context).getListTasks();        }        if (state is HomeSuccess) {          popScreen(context);        }      },      builder: (context, state) {        return Scaffold(          appBar: AppBar(            title: const Text(AppStrings.editeINAppBar),            actions: [              IconButton(                  onPressed: () => HomeCubit.get(context).editTask(                        context,                        item,                      ),                  icon: AppIcons.save)            ],          ),          body: SingleChildScrollView(              child: FormEdit(            item: item,          )),        );      },    );  }}