import 'package:bloc/bloc.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:meta/meta.dart';import 'package:tasky/Features/Home/data/repositories/home_repo.dart';import '../../../../Config/Route/next_screen.dart';import '../../../../Config/Route/route_app.dart';import '../../data/models/TaskModel.dart';part 'home_state.dart';class HomeCubit extends Cubit<HomeState> {  HomeCubit(this.homeRepo) : super(HomeInitial());  final HomeRepo homeRepo;  static HomeCubit get(context) => BlocProvider.of(context);  Future<void> getListTasks() async {    emit(HomeLoading());    await homeRepo.getAllDataTasks().then((value) {      value.fold((failure) => emit(HomeFailed(error: failure.errMessage)),          (user) => emit(HomeSuccess(model: user)));    });  }  Future<void> removeTask(context, String id) async {    emit(GetTaskLoading());    await homeRepo.removeTask(id: id).then((value) {      value.fold((failure) => emit(GetTaskFailed(error: failure.errMessage)),          (user) {        popScreen(context);        HomeCubit.get(context).getListTasks();        emit(GetTaskSuccess(model: user));      });    });  }  Future<void> logout(context) async {    emit(LogoutLoading());    await homeRepo.logout().then((value) {      value.fold((failure) => emit(LogoutFailed(error: failure.errMessage)),          (user) {        emit(LogoutSuccess(model: user));      });    });  }}