import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Core/Resources/styles.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import 'package:tasky/Features/Home/presentation/manager/home_cubit.dart';import '../widgets/build_button_tab_bar.dart';import '../widgets/custom_app_bar_home.dart';import '../widgets/tab_bar.dart';class TaskManagementScreen extends StatelessWidget {  const TaskManagementScreen({super.key});  @override  Widget build(BuildContext context) {    return BlocBuilder<HomeCubit, HomeState>(      builder: (context, state) {        if (state is HomeSuccess) {          return Scaffold(            appBar: buildAppBarTasks(context),            body: DefaultTabController(              length: 4,              child: Column(                children: [                  Row(                    children: [                      Text(                        AppStrings.titleList,                        style: getMediumText().copyWith(                            fontWeight: FontWeight.w700, fontSize: 18),                      ).paddingSH(30).paddingSV(15),                    ],                  ),                  const BuildButtonsTabBar(),                  ViewItemsTabBar(                    list: state.model,                  ),                ],              ),            ),          );        } else if (state is HomeFailed) {          return Scaffold(            body: Center(              child: Text(state.error),            ),          );        } else {          return const Scaffold(            body: Center(              child: CircularProgressIndicator(),            ),          );        }      },    );  }}