import 'package:flutter/material.dart';import '../../../data/models/task_model.dart';  import 'list_tem.dart';class ViewItemsTabBar extends StatefulWidget {  final List<TaskModel> list;  const ViewItemsTabBar({    super.key,    required this.list,  });  @override  State<ViewItemsTabBar> createState() => _ViewItemsTabBarState();}class _ViewItemsTabBarState extends State<ViewItemsTabBar> {  late List<TaskModel> all;  late List<TaskModel> waiting;  late List<TaskModel> inPro;  late List<TaskModel> finished;  @override  void initState() {    super.initState();    all = [];    waiting = [];    inPro = [];    finished = [];    for (final items in widget.list) {      if (items.status == "waiting") {        waiting.add(TaskModel.fromJson(items.toJson()));      } else if (items.status == "finish") {        finished.add(TaskModel.fromJson(items.toJson()));      } else if (items.status == "Inprogress") {        inPro.add(TaskModel.fromJson(items.toJson()));      }      all.add(TaskModel.fromJson(items.toJson()));    }  }  @override  Widget build(BuildContext context) {    return Expanded(      child: TabBarView(        children: [          TaskList(status: all),          TaskList(status: inPro),          TaskList(status: waiting),          TaskList(status: finished),        ],      ),    );  }}