import 'package:buttons_tabbar/buttons_tabbar.dart';import 'package:flutter/material.dart';import 'package:tasky/Core/Resources/string.dart';import '../../../../Core/Resources/colors.dart';import '../../../../Core/Resources/styles.dart';class BuildButtonsTabBar extends StatelessWidget {  const BuildButtonsTabBar({    super.key,  });  @override  Widget build(BuildContext context) {    return ButtonsTabBar(      contentPadding: const EdgeInsets.symmetric(horizontal: 10, vertical: 5),      physics: const BouncingScrollPhysics(),      backgroundColor: AppColors.colorPrimary,      unselectedBackgroundColor: Colors.grey[300],      unselectedLabelStyle: const TextStyle(color: AppColors.colorPrimaryText),      labelStyle: getBigText().copyWith(color: Colors.white, fontSize: 18),      radius: 20,      labelSpacing: 10,      onTap: (value){        print(value);      },      tabs: const [        Tab(          text: AppStrings.statusA,        ),        Tab(text: AppStrings.statusP),        Tab(text: AppStrings.statusW),        Tab(text: AppStrings.statusF),      ],    );  }}