import 'package:flutter/material.dart';import 'package:tasky/Config/Route/next_screen.dart';import 'package:tasky/Config/Route/route_app.dart';import 'package:tasky/Core/Resources/icons.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Core/Resources/styles.dart';import 'package:tasky/Features/Home/presentation/manager/home_cubit.dart';AppBar buildAppBarTasks(BuildContext context) {  return AppBar(    toolbarHeight: MediaQuery.of(context).size.height * .08,    title: Text(      AppStrings.titleINAppBar,      style: getBigText(),    ),    actions: [      IconButton(          onPressed: () => pushNext(context, Routes.routeProfile),          icon: AppIcons.profilePage),      IconButton(          onPressed: () => HomeCubit.get(context).logout(context),          icon: AppIcons.logoutPage),    ],  );}