import 'package:flutter/material.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';class TaskItem extends StatefulWidget {  final IconData icon;  final String title;  final String description;  final String status;  final String priority;  final String dueDate;  const TaskItem({    super.key,    required this.icon,    required this.title,    required this.description,    required this.status,    required this.priority,    required this.dueDate,  });  @override  _TaskItemState createState() => _TaskItemState();}class _TaskItemState extends State<TaskItem> {  bool _isTapped = false;  void _toggleTapped() {    setState(() {      _isTapped = !_isTapped;    });  }  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: _toggleTapped,      child: AnimatedContainer(        duration: const Duration(milliseconds: 300),        curve: Curves.easeInOut,        padding: const EdgeInsets.all(10),        decoration: BoxDecoration(          color: _isTapped ? Colors.blue.shade50 : Colors.white,          borderRadius: BorderRadius.circular(10),          boxShadow: [            if (_isTapped)              BoxShadow(                color: Colors.blue.shade100,                blurRadius: 10,                spreadRadius: 1,              ),          ],        ),        child: ListTile(          leading: AnimatedOpacity(            duration: const Duration(milliseconds: 300),            opacity: _isTapped ? 0.5 : 1,            child: Icon(widget.icon),          ),          title: Row(            mainAxisAlignment: MainAxisAlignment.spaceBetween,            children: [              AnimatedOpacity(                duration: const Duration(milliseconds: 300),                opacity: _isTapped ? 0.5 : 1,                child: Text(widget.title),              ),              Text(widget.status),            ],          ).paddingSH(15),          subtitle: Column(            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Text(widget.description),              Row(                mainAxisAlignment: MainAxisAlignment.spaceBetween,                children: [                  Row(                    children: [                      const Icon(Icons.outlined_flag_outlined),                      Text(widget.priority),                    ],                  ),                  Text(widget.dueDate),                ],              ),            ],          ).paddingSH(15),          trailing: const Icon(Icons.more_vert_outlined),        ),      ),    );  }}