import 'package:flutter/material.dart';import '../../../../Config/asset.dart';import '../../../../Core/Utils/Widget/build_image.dart';import '../widgets/body_login.dart';class LoginScreen extends StatefulWidget {  const LoginScreen({super.key});  @override  State<LoginScreen> createState() => _LoginScreenState();}class _LoginScreenState extends State<LoginScreen>    with SingleTickerProviderStateMixin {  late AnimationController _controller;  late Animation<Offset> _slideAnimation;  @override  void initState() {    super.initState();    _controller = AnimationController(      duration: const Duration(seconds: 1),      vsync: this,    );    animation();    _controller.forward();  }  Animation<Offset> animation() {    return _slideAnimation = Tween<Offset>(    begin: const Offset(3, 0), // Start from the right    end: Offset.zero,  ).animate(    CurvedAnimation(      parent: _controller,      curve: Curves.easeInOut,    ),  );  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(          child: SingleChildScrollView(        child: Column(          children: [            BuildImageAssets(              height: MediaQuery.of(context).size.height * .58,              typeSVg: false,              url: AppAssets.home,            ),            AnimatedBuilder(              animation: _controller,              builder: (context, child) => SlideTransition(                position: _slideAnimation,                child: const BuildBodyLogin(),              ),            ),          ],        ),      )),    );  }}