import 'package:dartz/dartz.dart';import 'package:dio/dio.dart';import 'package:tasky/Core/Network/Local/cachehelper.dart';import 'package:tasky/Core/Utils/Errors/failure.dart';import 'package:tasky/Features/Profile/data/models/ProfileModel.dart';import '../../../../Core/Network/Remote/dio.dart';import '../../../../Core/Network/Remote/endpoints.dart';import '../../../../Core/Resources/auth.dart';import '../../data/repositories/profile_reo.dart';class ProfileRepoImpl implements ProfileReo {  @override  Future<Either<Failure, ProfileModel>> fetchLogin() async {    try {      String token = CashHelper.getData(        key: 'Token',      );      var data =          await DioHelper.getData(path: AppEndPoint.profile, token: token);      return right(ProfileModel.fromJson(data.data));    } catch (e) {      if (e is DioException) {        if (e.response?.statusCode == 401) {          String? newToken = await refreshToken();          if (newToken != null) {            return fetchLogin();          } else {            return left(ServerFailure.fromDioError(e));          }        } else {          return left(ServerFailure.fromDioError(e));        }      } else {        return left(ServerFailure.fromResponse(e.hashCode, e));      }    }  }}