import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Features/Profile/data/models/profile_model.dart';import 'package:tasky/Features/Profile/data/repositories/profile_reo.dart';part 'profile_state.dart';class ProfileCubit extends Cubit<ProfileState> {  ProfileCubit(this.profileReo) : super(ProfileInitial());  final ProfileReo profileReo;  static ProfileCubit get(context) => BlocProvider.of(context);  Future<void> getData() async {    emit(ProfileLoading());    profileReo.fetchLogin().then((value) {      value.fold((failure) => emit(ProfileFailed(error: failure.errMessage)),          (user) => emit(ProfileSuccess(model: user)));    });  }}