import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import 'package:tasky/Core/Utils/Widget/custom_app_bar.dart';import 'package:tasky/Core/Utils/Widget/custom_massage.dart';import 'package:tasky/Features/Profile/presentation/manager/profile_cubit.dart';import '../widgets/build_profile_fields.dart';class ProfileScreen extends StatelessWidget {  const ProfileScreen({super.key});  @override  Widget build(BuildContext context) {    return BlocBuilder<ProfileCubit, ProfileState>(      builder: (context, state) {        if (state is ProfileSuccess) {          return Scaffold(            appBar: buildCustomAppBar(title: 'Profile'),            body: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                ProfileField(label: "Name", value: state.model.username!),                ProfileField(                    label: 'PHONE', value: state.model.displayName!, isPhone: true),                ProfileField(label: 'LEVEL', value: state.model.level!),                ProfileField(label: 'YEARS OF EXPERIENCE', value: '${state.model.experienceYears!} years'),                ProfileField(label: 'LOCATION', value: state.model.address!),              ],            ).paddingAll(16),          );        }        else if (state is ProfileFailed) {          return CustomMassage(massage: state.error);        } else {          return const Center(            child: CircularProgressIndicator(),          );        }      },    );  }}