import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Features/QR/domain/use_cases/qr_use_case.dart';import '../../../Home/data/models/task_model.dart';part 'qr_state.dart';class QrCubit extends Cubit<QrState> {  QrCubit(this.qrUseCase) : super(QrInitial());  final QrUseCase qrUseCase;  static QrCubit get(context) => BlocProvider.of(context);  Future<void> getOneTask(String id) async {    emit(GetOneTaskLoading());    await qrUseCase.execute(id: id).then((value) {      value.fold((failure) {        emit(GetOneTaskFailed(error: failure.errMessage));      }, (task) => emit(GetOneTaskSuccess(model: task)));    });  }}