import 'dart:ui';import 'package:flutter/material.dart';import 'package:tasky/Core/Utils/Widget/qr_build.dart';import '../../Config/Route/next_screen.dart';import '../../Core/Resources/colors.dart';class ViewQr extends StatelessWidget {  final String item ;  const ViewQr({super.key, required this.item});  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.transparent,      body: Stack(        children: [          InkWell(              onTap: () => popScreen(context),              splashColor: Colors.transparent,              highlightColor: Colors.transparent,              child: BackdropFilter(                filter: ImageFilter.blur(sigmaX: 10, sigmaY: 10),                blendMode: BlendMode.srcOver,                child: Container(                    color: AppColors.colorsBackGround.withOpacity(0.3),                    child: Center(                        child: Padding(                          padding: const EdgeInsets.only(top: 40),                          child: QrBuilding(item: item),                        ))),              )),        ],      ),    );  }}