import 'package:flutter/material.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import '../../../../Config/asset.dart';import '../../../../Core/Utils/Widget/build_image.dart';import '../widgets/body_sing_up.dart';class SingUpScreen extends StatefulWidget {  const SingUpScreen({super.key});  @override  State<SingUpScreen> createState() => _SingUpScreenState();}class _SingUpScreenState extends State<SingUpScreen>    with SingleTickerProviderStateMixin {  late AnimationController _controller;  late Animation<Offset> _slideAnimation;  @override  void initState() {    super.initState();    _controller = AnimationController(      duration: const Duration(seconds: 1),      vsync: this,    );    animation();    _controller.forward();  }  Animation<Offset> animation() {    return _slideAnimation = Tween<Offset>(    begin: const Offset(3, 0), // Start from the right    end: Offset.zero,  ).animate(    CurvedAnimation(      parent: _controller,      curve: Curves.easeInOut,    ),  );  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: SafeArea(          child: SingleChildScrollView(        child: Column(          children: [            BuildImageAssets(              height: MediaQuery.of(context).size.height * .25,              typeSVg: false,              url: AppAssets.home,            ),            AnimatedBuilder(              animation: _controller,              builder: (context, child) => SlideTransition(                position: _slideAnimation,                child: const BuildBodySingUp().paddingSH(15),              ),            ),          ],        ),      )),    );  }}