import 'package:dropdown_button2/dropdown_button2.dart';import 'package:flutter/material.dart';import 'package:tasky/Core/Resources/styles.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import 'package:tasky/Features/SignUp/presentation/manager/sing_up_cubit.dart';import '../../../../Core/Resources/colors.dart';class ExperienceDropdown extends StatelessWidget {  final ValueChanged onChanged;  const ExperienceDropdown({    super.key,    required this.onChanged,  });  @override  Widget build(BuildContext context) {    return Container(      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(20),        border: Border.all(color: AppColors.colorTextFieldBorder),      ),      child: DropdownButtonHideUnderline(        child: DropdownButton2(          enableFeedback: true,          style: getTextField(),          hint: Text(            'Choose experience Level',            style: getTextField().copyWith(                fontWeight: FontWeight.bold, color: AppColors.colorsText1),          ),          items: _list,          value: SingUpCubit.get(context).selectedValue,          onChanged: onChanged,          isExpanded: true,        ),      ),    ).paddingAll(15);  }}List<DropdownMenuItem> _list = [  DropdownMenuItem(    value: 'fresh',    child: Text('Fresh', style: getTextField()),  ),  DropdownMenuItem(    value: 'junior',    child: Text('Junior', style: getTextField()),  ),  DropdownMenuItem(    value: 'midLevel',    child: Text('MidLevel  ', style: getTextField()),  ),  DropdownMenuItem(    value: 'senior',    child: Text('Senior', style: getTextField()),  ),];