import 'package:flutter/material.dart';import 'package:tasky/Config/Route/next_screen.dart';import 'package:tasky/Config/Route/route_app.dart';import 'package:tasky/Config/asset.dart';import 'package:tasky/Core/Network/Local/cachehelper.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Core/Utils/Extensions/padding.dart';import 'package:tasky/Core/Utils/Widget/build_image.dart';import '../../Core/Resources/styles.dart';import '../../Core/Utils/Widget/build_buttom.dart';class StartScreen extends StatefulWidget {  const StartScreen({super.key});  @override  State<StartScreen> createState() => _StartScreenState();}class _StartScreenState extends State<StartScreen>    with SingleTickerProviderStateMixin {  late AnimationController _controller;  late Animation<double> _fadeAnimation;  late Animation<Offset> _slideAnimation;  @override  void initState() {    super.initState();    _controller = AnimationController(      duration: const Duration(seconds: 2),      vsync: this,    );    _fadeAnimation = Tween<double>(begin: 0, end: 1).animate(      CurvedAnimation(        parent: _controller,        curve: Curves.easeIn,      ),    );    _slideAnimation = Tween<Offset>(      begin: const Offset(0, 1),      end: Offset.zero,    ).animate(      CurvedAnimation(        parent: _controller,        curve: Curves.easeInOut,      ),    );    _controller.forward();  }  @override  void dispose() {    _controller.dispose();    super.dispose();  }  @override  Widget build(BuildContext context) {    return Scaffold(      body: AnimatedBuilder(        animation: _controller,        builder: (context, child) => _buildBody().paddingAll(20),      ),    );  }  Column _buildBody() => Column(        mainAxisAlignment: MainAxisAlignment.center,        children: [          FadeTransition(            opacity: _fadeAnimation,            child: BuildImageAssets(              height: MediaQuery.of(context).size.height * .58,              typeSVg: false,              url: AppAssets.home,            ),          ),          Expanded(            child: Column(              children: [                SlideTransition(                  position: _slideAnimation,                  child: Text(                    AppStrings.titleINStart,                    style: getBigText(),                    maxLines: 2,                    textAlign: TextAlign.center,                  ),                ),                SlideTransition(                  position: _slideAnimation,                  child: Text(                    AppStrings.descriptionINStart,                    style: getMediumText(),                    textAlign: TextAlign.center,                    maxLines: 3,                  ).paddingAll(20),                ),                FadeTransition(                  opacity: _fadeAnimation,                  child: DefaultButton(                    width: MediaQuery.of(context).size.width * .8,                    press: () {                      CashHelper.saveData(key: 'StartKey', value: true)                          .then((_) {                        pushReplacement(context, Routes.routeLogin);                      });                    },                    text: AppStrings.start,                    condition: true,                  ),                ),              ],            ),          ),        ],      );}