import 'package:flutter/material.dart';import 'package:tasky/Core/Resources/icons.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Features/Home/presentation/manager/home_cubit.dart';class BuildPopupMenuButton extends StatelessWidget {  final String id;  final bool pop;  const BuildPopupMenuButton({    super.key,    required this.id, required this.pop,  });  @override  Widget build(BuildContext context) {    return PopupMenuButton(      shape: OutlineInputBorder(          borderRadius: BorderRadius.circular(20),          borderSide: const BorderSide(width: 0, color: Colors.transparent)),      icon: AppIcons.moreVert,      offset: const Offset(50, 50),      itemBuilder: (BuildContext context) => [        const PopupMenuItem(          value: AppStrings.edite,          child: Text(AppStrings.edite),        ),        PopupMenuItem(          value: AppStrings.delete,          onTap: () => HomeCubit.get(context).removeTask(context, id,pop ),          child: const Text(            AppStrings.delete,            style: TextStyle(color: Colors.red),          ),        ),      ],    );  }}