import 'package:flutter_bloc/flutter_bloc.dart';import 'package:tasky/Features/Profile/data/models/user_model.dart';import '../../domain/use_cases/user_use_case.dart';part 'user_state.dart';class UserCubit extends Cubit<UserState> {  UserCubit(this.userUseCase) : super(ProfileInitial());  final UserUseCase userUseCase;  static UserCubit get(context) => BlocProvider.of(context);  Future<void> getData() async {    emit(ProfileLoading());    userUseCase.execute().then((value) {      value.fold((failure) => emit(ProfileFailed(error: failure.errMessage)),          (user) => emit(ProfileSuccess(model: user)));    });  }}