import 'package:flutter/material.dart';import 'package:flutter/services.dart';import 'package:tasky/Core/Resources/colors.dart';import 'package:tasky/Core/Resources/icons.dart';import 'package:tasky/Core/Resources/string.dart';import 'package:tasky/Core/Resources/styles.dart';import 'package:tasky/Core/Utils/Widget/custom_scaffold_messenger.dart';class ProfileField extends StatelessWidget {  final String label;  final String value;  final bool isPhone;  const ProfileField(      {super.key,      required this.label,      required this.value,      this.isPhone = false});  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(8.0),      child: Container(        color: AppColors.colorBackGroundInFieldsProfile,        padding: const EdgeInsets.all(10),        child: Column(          crossAxisAlignment: CrossAxisAlignment.start,          children: [            Text(              label,              style: getSmallText1(),            ),            Row(              children: [                Expanded(                  child: Text(                    value,                    style: getSmallText2(),                  ),                ),                if (isPhone)                  IconButton(                      icon: AppIcons.copyIcons,                      onPressed: () {                        Clipboard.setData(ClipboardData(text: value));                        showSnackBar(context, AppStrings.copyMessage);                        // Add your edit functionality here},                      }),              ],            ),          ],        ),      ),    );  }}